import React, { useState } from 'react';
import * as XLSX from 'xlsx';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    year: '',
  });
  const [registrations, setRegistrations] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setRegistrations([...registrations, formData]);
    setFormData({
      name: '',
      email: '',
      year: '',
    });
  };

  const handleExport = () => {
    const worksheet = XLSX.utils.json_to_sheet(registrations);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Alumni');
    XLSX.writeFile(workbook, 'AlumniRegistrations.xlsx');
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <h1 className="text-3xl font-bold mb-6">Alumni Registration</h1>
      <div className="bg-white p-8 rounded shadow-md w-full max-w-lg">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Name:</label>
            <input
              type="text"
              name="name"
              placeholder="Enter your name"
              value={formData.name}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-2">Email:</label>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded"
              required
            />
          </div>
          <div>
            <label className="block text-gray-700 font-medium mb-2">Graduation Year:</label>
            <input
              type="text"
              name="year"
              placeholder="Enter your graduation year"
              value={formData.year}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full py-2 px-4 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
          >
            Register
          </button>
        </form>
        <div className="mt-8">
          <h2 className="text-xl font-bold mb-4">Registered Alumni</h2>
          <ul>
            {registrations.map((registration, index) => (
              <li key={index} className="mb-2 p-2 border-b">
                <p><strong>Name:</strong> {registration.name}</p>
                <p><strong>Email:</strong> {registration.email}</p>
                <p><strong>Graduation Year:</strong> {registration.year}</p>
              </li>
            ))}
          </ul>
          <button
            onClick={handleExport}
            className="mt-4 py-2 px-4 bg-green-600 text-white rounded hover:bg-green-700 transition"
          >
            Export to Excel
          </button>
        </div>
      </div>
    </div>
  );
};

export default Contact;
